mapper.select:
select

from h_user
where is_del = 0

  AND user_name = ${name}


  AND mobile = ${mobile}

ORDER BY c_time

  ASC


  DESC

limit #{offset},#{limit};

select COUNT(*) from h_user where is_del = 0
AND user_name = ${name}
AND mobile = ${mobile}
ORDER BY c_time
ASC
DESC

select , SUM(`h_order`.`r_total`) AS `cost_sum` FROM ((`h_user` LEFT JOIN `h_order` ON ((`h_order`.`user_id` = `h_user`.`id` AND h_order.`status` != 0))) ) GROUP BY `h_user`.`id` ORDER BY `cost_sum`
ASC
DESC ;
